import { useState } from "react";
import Board from "./Board";
import Button from "./Button";
import "./App.css";
import logo from "./diceImage/logo.png";

function random(n) {
  return Math.ceil(Math.random() * n);
}

function App() {
  const [myHistory, setMyHistory] = useState([]);
  const [otherHistory, setOtherHistory] = useState([]);

  const handleRollClick = () => {
    const nextMyNum = random(6);
    const nextOtherNum = random(6);
    setMyHistory([...myHistory, nextMyNum]);
    setOtherHistory([...otherHistory, nextOtherNum]);
  };

  const handleClearClick = () => {
    setMyHistory([]);
    setOtherHistory([]);
  };

  return (
    <div className="App">
      <div>
        <img className="App-logo" src={logo} alt="주사위게임 로고" />
        <h1 className="App-title">주사위게임</h1>
        <div>
          <Button
            className="Button blue App-button"
            color="blue"
            onClick={handleRollClick}
          >
            던지기
          </Button>
          <Button
            className="utton red App-button"
            color="red"
            onClick={handleClearClick}
          >
            처음부터
          </Button>
        </div>
      </div>
      <div className="App-boards">
        <div></div>
        <div class="Board App-board">
          <h2 class="Board-heading">상대</h2>
          <img class="Dice" src="dice-red-1.svg" alt="1" />
          <h2 class="Board-heading">기록</h2>
          <p></p>
          <h2 class="Board-heading">총점</h2>
          <p>0</p>
        </div>
        <Board
          className="Board App-board"
          name="나"
          color="blue"
          gameHistory={myHistory}
        />
        <Board
          className="Board App-board"
          name="상대"
          color="red"
          gameHistory={otherHistory}
        />
      </div>
    </div>
  );
}

export default App;
